mapper.select:
select id, adminName, nickName, email, status, createTime, loginTime, adminImg from admin


        and id = #{id}


        and adminName like concat('%', #{adminName}, '%')


        and nickName like concat('%', #{nickName}, '%')


        and email = #{email}


        and status = #{status}


order by createTime desc


    limit #{page}, #{pageSize};
